public List<Integer> answer(int[] table, int[] select, int each) {
  List list = new ArrayList();
  for(int selectIndex = 0; selectIndex < select.length; selectIndex++) {
    if(select[selectIndex] == 0) continue;
    for(int k= each; k > 0; k--) {
      int[][] dp = new int[k+1][k+1][];
      for(int row=0; row < k+1; row++) for(int col = 0; col < k+1; col++) {
        if(row == 0 || k == 1 || row == col) dp[col][row] = new int[]{selectIndex+1, table[selectIndex]};
        else {
          int maxChosen = 0;
          for(int i = dp[col][k-1][0]; i <= dp[col][k-1][1]; i++) {
            if(dp[col][i][0] > maxChosen) maxChosen = dp[col][i][0];
            if(table[i] > maxChosen || maxChosen == dp[col][k-1][1]) break;
          }
          if(maxChosen <= dp[col][k-1][1]) dp[col][row] = new int[]{dp[col][k-1][0], maxChosen, table[maxChosen], ++maxChosen};
        }
      }
      Select selectIndex = new Select(selectIndex, each-k);
      if(Table.isNumeric(selectIndex)) {
        List seq = list.stream().filter((a) -> a.compareTo(selectIndex) >= 0)
            .collect(Collectors.toList());
        if(seq.size() > 0)
          for(Select s : seq)
            if(s.compareTo(selectIndex) - s.minute < 0 && (int)s.from != selectIndex)
              list
                  .stream()
                  .filter((i) -> (int)i.from <= s.from)
                  .sorted()
                  .distinct(Integer::compareTo)
                  .findFirst().ifPresent(selectIndex::addSecond);
      } else list.add(selectIndex);
    }
  }
  return list;
}




