    private static void FitlerPlaintextToLimits(string plainText)
    {
        while (plainText.Contains(' ', '\n', '\r', ',', '.') &&
               plainText.Length > 20 &&
               plainText.Length < 50)
        {
            plainText = plainText.Replace(' ', '-');
            plainText = plainText.Replace('\n', '', 'g');
            plainText = plainText.Replace('\r', '', 'g');
            plainText = plainText.Replace(',', '', 'g');
            plainText = plainText.Replace('.', '', 'g');
        }
        plainText = plainText.Remove(plainText.Length - 1, 1);
    }
}

